import React from 'react';
import './CovidTracker.scss';

// Change this value for the speed of the animations.

// Change this value for the number of bars (value) in the array.

// This is the main color of the array bars.


export default class CovidTracker extends React.Component {
    constructor(props) {
        super(props);

        this.state = {summary: [],
        countries:[],
        country_data:[]};
    }
    componentDidMount(){
        fetch('https://api.covid19api.com/all')
  .then((response) => {
    return response.json();
  })
  .then((all) => {
      let countries = [];
      const country_data = [];
      all.forEach(element => {
          if (!countries.includes(element.Country)){
            countries.push(element.Country);
            country_data.push({'country': element.Country,"confirmed": [element.Confirmed],
            "deaths": [element.Deaths],
            "recovered": [element.Recovered],
            "active": [element.Active],
            "dates": [element.Date]})
          }
          else{
              country_data[country_data.length-1].confirmed.push(element.Confirmed);
              country_data[country_data.length-1].deaths.push(element.Deaths);
              country_data[country_data.length-1].recovered.push(element.Recovered);
              country_data[country_data.length-1].active.push(element.Sctive);
              country_data[country_data.length-1].dates.push(element.Date);
          }    
      });
      this.setState({countries});
      this.setState({country_data});
    console.log(this.state.countries);
    console.log(this.state.country_data);

  });
    }
    updatePlot(){
        
		var lineChartData = {
			labels: this.state.country_data[25].dates,
			datasets: [{
				label: '#Confirmed',
				borderColor: window.chartColors.red,
				backgroundColor: window.chartColors.red,
				fill: false,
				data: this.state.country_data[25].cofirmed,
				yAxisID: 'confirmed-axis',
			}, {
				label: '#Deaths',
				borderColor: window.chartColors.blue,
				backgroundColor: window.chartColors.blue,
				fill: false,
				data: this.state.country_data[25].deaths,
				yAxisID: 'death-axis'
            }
            , {
				label: '#Recovered',
				borderColor: window.chartColors.blue,
				backgroundColor: window.chartColors.blue,
				fill: false,
				data: this.state.country_data[25].recovered,
				yAxisID: 'recovered-axis'
            }
        ]
		};

		window.onload = function() {
			var ctx = document.getElementById('canvas').getContext('2d');
			window.myLine = Chart.Line(ctx, {
				data: lineChartData,
				options: {
					responsive: true,
					hoverMode: 'index',
					stacked: false,
					title: {
						display: true,
						text: 'Chart.js Line Chart - Multi Axis'
					},
					scales: {
						yAxes: [{
							type: 'linear', // only linear but allow scale type registration. This allows extensions to exist solely for log scale for instance
							display: true,
							position: 'left',
							id: 'confirmed-axis',
						}, {
							type: 'linear', // only linear but allow scale type registration. This allows extensions to exist solely for log scale for instance
							display: true,
							position: 'right',
							id: 'death-axis',

							// grid line settings
							gridLines: {
								drawOnChartArea: false, // only want the grid lines for one axis to show up
							},
						}],
					}
				}
			});
		};

		
	
    }

    render() {
        return (
            <React.Fragment>
                <div className="grid-container">
                    <div className="header">
                        <h1>Covid19 Tracker App</h1>
                        <p>Coming Soon ...</p>
                    </div>
                </div>
                <div className="footer">
                    <p><b>Visualizing Covid19 data, Developed with React Framework. Written by Ashkan Bashiri.</b></p>
                </div>
            </React.Fragment>
        )
    }
}
