import React from "react";
import "./CovidTracker.scss";
import { Line } from "react-chartjs-2";
import Select from "react-select";

// Change this value for the speed of the animations.

// Change this value for the number of bars (value) in the array.

// This is the main color of the array bars.

export default class CovidTracker extends React.Component {
  constructor(props) {
    super(props);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleCountryChange = this.handleCountryChange.bind(this);

    this.state = {
      summary: [],
      countries: [],
      country_data: [],
      labels: [],
      datasets: [],
      selectedCountry: "China",
      selectedProvince: "Hubei",
      options: [],
      prov_options: [],
    };
  }
  handleProvinceChange = (selectedOption) => {
    this.setState({ selectedProvince: selectedOption.value }, () => {
      const select_ = this.state.country_data.filter(
        (element) =>
          element.country == this.state.selectedCountry &&
          element.province == this.state.selectedProvince
      );
      var labels = select_[0].dates;
      var datasets = [
        {
          label: "#Confirmed",
          fill: false,
          lineTension: 0.5,
          backgroundColor: "rgba(33,47,61,1)",
          borderColor: "rgba(33,47,61,1)",
          borderWidth: 1,
          data: select_[0].confirmed,
        },
        {
          label: "#Deaths",
          fill: false,
          lineTension: 0.5,
          backgroundColor: "rgba(205,92,92,1)",
          borderColor: "rgba(205,92,92,1)",
          borderWidth: 1,
          data: select_[0].deaths,
        },
        {
          label: "#Recovered",
          fill: false,
          lineTension: 0.5,
          backgroundColor: "rgba(72,201,176,1)",
          borderColor: "rgba(72,201,176,1)",
          borderWidth: 1,
          data: select_[0].recovered,
        },
      ];
      this.setState({ labels: labels, datasets: datasets }, () => {
        this.setState({ labels: labels, datasets: datasets });
      });
    });
  };

  handleCountryChange = (selectedOption) => {
    this.setState({ selectedCountry: selectedOption.value }, () => {
      console.log(this.state.country_data);
      console.log(this.state.selectedCountry);
      const select_ = this.state.country_data.filter(
        (element) =>
          element.country == this.state.selectedCountry &&
          element.province == this.state.selectedProvince
      );
      console.log(select_);
      var labels = select_[0].dates;
      console.log(labels);
      var datasets = [
        {
          label: "#Confirmed",
          fill: false,
          lineTension: 0.5,
          backgroundColor: "rgba(33,47,61,1)",
          borderColor: "rgba(33,47,61,1)",
          borderWidth: 1,
          data: select_[0].confirmed,
        },
        {
          label: "#Deaths",
          fill: false,
          lineTension: 0.5,
          backgroundColor: "rgba(205,92,92,1)",
          borderColor: "rgba(205,92,92,1)",
          borderWidth: 1,
          data: select_[0].deaths,
        },
        {
          label: "#Recovered",
          fill: false,
          lineTension: 0.5,
          backgroundColor: "rgba(72,201,176,1)",
          borderColor: "rgba(72,201,176,1)",
          borderWidth: 1,
          data: select_[0].recovered,
        },
      ];
      this.setState({ labels: labels, datasets: datasets }, () => {
        this.setState({ labels: labels, datasets: datasets });
      });
    });
  };
  componentDidMount() {
    const options = [];
    const prov_options = [];
    const months = [
      "Jan ",
      "Feb ",
      "Mar ",
      "Apr ",
      "May ",
      "Jun ",
      "Jul ",
      "Aug ",
      "Sep ",
      "Oct ",
      "Nov ",
      "Dec ",
    ];

    fetch("https://api.covid19api.com/all")
      .then((response) => {
        return response.json();
      })
      .then((all) => {
        const country_province = [{}];
        let countries = [];
        let provinces = [];
        const country_data = [];
        all.forEach((element) => {
          let province = element.Province === "" ? "ALL" : element.Province;
          console.log(province);
          if (
            !countries.includes(element.Country) &&
            !provinces.includes(province)
          ) {
            countries.push(element.Country);
            provinces.push(province);
            let date_ = new Date(element.Date);
            country_data.push({
              country: element.Country,
              prvince: province,
              confirmed: [element.Confirmed],
              deaths: [element.Deaths],
              recovered: [element.Recovered],
              active: [element.Active],
              dates: [months[date_.getMonth()] + date_.getDate()],
            });
            options.push({ value: element.Country, label: element.Country });
            prov_options.push({
              value: province,
              label: province,
            });
          } else if (
            country_province
              .filter((c_p) => c_p.country === element.Country)
              .provinces.includes(province)
          ) {
          } else {
            country_data[country_data.length - 1].confirmed.push(
              element.Confirmed
            );
            country_data[country_data.length - 1].deaths.push(element.Deaths);
            country_data[country_data.length - 1].recovered.push(
              element.Recovered
            );
            let date_ = new Date(element.Date);
            country_data[country_data.length - 1].active.push(element.Active);
            country_data[country_data.length - 1].dates.push(
              months[date_.getMonth()] + date_.getDate()
            );
          }
        });
        this.setState({ options });
        this.setState({ prov_options });
        this.setState({ countries });
        this.setState({ country_data });
      });
  }
  rnd_range(mn, mx) {
    return Math.random() * (mx - mn) + mn;
  }

  handleSubmit() {
    let random_country = this.state.countries[
      Math.floor(this.rnd_range(0, this.state.countries.length)) - 1
    ];

    this.setState({ selectedCountry: random_country }, () => {
      console.log(this.state.selectedCountry);

      const select_ = this.state.country_data.filter(
        (element) => element.country == this.state.selectedCountry
      );
      var labels = select_[0].dates;
      console.log(labels);

      var datasets = [
        {
          label: "#Confirmed",
          fill: false,
          lineTension: 0.5,
          backgroundColor: "rgba(33,47,61,1)",
          borderColor: "rgba(33,47,61,1)",
          borderWidth: 1,
          data: select_[0].confirmed,
        },
        {
          label: "#Deaths",
          fill: false,
          lineTension: 0.5,
          backgroundColor: "rgba(205,92,92,1)",
          borderColor: "rgba(205,92,92,1)",
          borderWidth: 1,
          data: select_[0].deaths,
        },
        {
          label: "#Recovered",
          fill: false,
          lineTension: 0.5,
          backgroundColor: "rgba(72,201,176,1)",
          borderColor: "rgba(72,201,176,1)",
          borderWidth: 1,
          data: select_[0].recovered,
        },
      ];
      this.setState({ labels: labels, datasets: datasets }, () => {
        this.setState({ labels: labels, datasets: datasets });
      });
    });
  }

  render() {
    return (
      <React.Fragment>
        <div className="grid-container">
          <div className="header">
            <h1>Covid19 Tracker App</h1>
          </div>
          <div className="left">
            <Select
              onChange={this.handleCountryChange}
              options={this.state.options}
              autoFocus={true}
            />
            <Select
              onChange={this.handleProvinceChange}
              options={this.state.prov_options}
              autoFocus={true}
            />
          </div>
          <div className="middle">
            <Line
              data={this.state}
              options={{
                title: {
                  display: true,
                  text: "Daily statistics for " + this.state.selectedCountry,
                  fontSize: 20,
                },
                legend: {
                  display: true,
                  position: "right",
                },
              }}
            />
          </div>
        </div>
        <div className="footer">
          <p>
            <b>
              Visualizing Covid19 data, Developed with React Framework. Written
              by Ashkan Bashiri.
            </b>
          </p>
        </div>
      </React.Fragment>
    );
  }
}
