import React from 'react';
import SortingVisualizer from './SortingVisualizer/SortingVisualizer';
import './App.scss';
import 'bootstrap/dist/css/bootstrap.min.css';
import { BrowserRouter as Router, HashRouter, Route } from 'react-router-dom';
import Header from './layout/Header';
import MachineLearning from './MachineLearning/MachineLearning'

function App() {
  document.body.getElementById("dark-mode-toggle").addEventListener(themeChange);
  document.body.classList.remove("darkmode");

  function themeChange(){
    switch(this.state.theme){
      case "light": 
        document.body.classList.add("darkmode");
        this.setState({theme: "dark"
        });
      
      case "dark": 
        document.body.classList.remove("darkmode");
        this.setState({theme: "light"
        });
  }
    }

  return (
    <div className="App">
      <button id="dark-mode-toggle" class="sticky_btn">Theme</button>
      <HashRouter>
        <Header />
        <Route
          exact
          path="/" component={SortingVisualizer}
        />
        <Route
          path="/sv" component={SortingVisualizer}
        />
        <Route path="/ml" component={MachineLearning} />

      </HashRouter>
    </div>
  );
}

export default App;
