import React from "react";
import "./CovidTracker.scss";
import { Line } from "react-chartjs-2";

// Change this value for the speed of the animations.

// Change this value for the number of bars (value) in the array.

// This is the main color of the array bars.

export default class CovidTracker extends React.Component {
  constructor(props) {
    super(props);

    this.state = { summary: [], countries: [], country_data: [] };
  }
  componentDidMount() {
    fetch("https://api.covid19api.com/all")
      .then((response) => {
        return response.json();
      })
      .then((all) => {
        let countries = [];
        const country_data = [];
        all.forEach((element) => {
          if (!countries.includes(element.Country)) {
            countries.push(element.Country);
            country_data.push({
              country: element.Country,
              confirmed: [element.Confirmed],
              deaths: [element.Deaths],
              recovered: [element.Recovered],
              active: [element.Active],
              dates: [element.Date],
            });
          } else {
            country_data[country_data.length - 1].confirmed.push(
              element.Confirmed
            );
            country_data[country_data.length - 1].deaths.push(element.Deaths);
            country_data[country_data.length - 1].recovered.push(
              element.Recovered
            );
            country_data[country_data.length - 1].active.push(element.Sctive);
            country_data[country_data.length - 1].dates.push(element.Date);
          }
        });
        this.setState({ countries });
        this.setState({ country_data });
        console.log(this.state.countries);
        console.log(this.state.country_data);
      });
  }

  render() {
    return (
      <React.Fragment>
        <div className="grid-container">
          <div className="header">
            <h1>Covid19 Tracker App</h1>
            <Line
              data={state}
              options={{
                title: {
                  display: true,
                  text: "Average Rainfall per month",
                  fontSize: 20,
                },
                legend: {
                  display: true,
                  position: "right",
                },
              }}
            />
          </div>
        </div>
        <div className="footer">
          <p>
            <b>
              Visualizing Covid19 data, Developed with React Framework. Written
              by Ashkan Bashiri.
            </b>
          </p>
        </div>
      </React.Fragment>
    );
  }
}
