import React from "react";
import { MDBDataTable } from "mdbreact";
export default class CovidSummary extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
    };
  }

  componentDidMount() {
    const data = {
      columns: [
        {
          label: "country",
          field: "country",
          sort: "asc",
          width: 150,
        },
        {
          label: "totalConfirmed",
          field: "totalConfirmed",
          sort: "asc",
          width: 150,
        },
        {
          label: "newConfirmed",
          field: "newConfirmed",
          sort: "asc",
          width: 150,
        },
        {
          label: "totalDeaths",
          field: "totalDeaths",
          sort: "asc",
          width: 150,
        },
        {
          label: "newDeaths",
          field: "newDeaths",
          sort: "asc",
          width: 150,
        },
        {
          label: "totalRecovered",
          field: "totalRecovered",
          sort: "asc",
          width: 150,
        },
        {
          label: "newRecovered",
          field: "totalRecovered",
          sort: "asc",
          width: 150,
        },
        {
          label: "lastUpdate",
          field: "lastUpdate",
          sort: "asc",
          width: 150,
        },
      ],
      rows: [],
    };
    fetch("https://api.covid19api.com/summary")
      .then((response) => {
        return response.json();
      })
      .then((all) => {
        let global = {
          country: "Global",
          totalConfirmed: all.Global.TotalConfirmed,
          newConfirmed: all.Global.NewConfirmed,
          totalDeaths: all.Global.TotalDeaths,
          newDeaths: all.Global.NewDeaths,
          totalRecovered: all.Global.TotalRecovered,
          newRecovered: all.Global.NewRecovered,
          lastUpdate: "",
        };

        this.setState({ data });
      });
  }
  render() {
    return <MDBDataTable striped bordered small data={this.state.data} />;
  }
}
