import React, {useState} from 'react';
import SortingVisualizer from './SortingVisualizer/SortingVisualizer';
import './App.scss';
import 'bootstrap/dist/css/bootstrap.min.css';
import { BrowserRouter as Router, HashRouter, Route } from 'react-router-dom';
import Header from './layout/Header';
import MachineLearning from './MachineLearning/MachineLearning'
import ToggleButton from 'react-toggle-button'


function App() {
  const [theme, setTheme] = useState("light")
  const [themeValue, setThemeValue] = useState(false)

  return (
    <div className="App">
     
      
      <HashRouter>
      
        <Header />
        <span>Dark Mode</span>
        <ToggleButton id="dark-mode-toggle2" className="sticky_btn"
  value={themeValue}
  onToggle={() => {
    switch(theme){
      case "light": {
      console.log("Changing theme from light to dark")
        document.body.classList.add("darkmode");
        setTheme("dark");
        setThemeValue(true)
        break;
      }
      case "dark": {
        console.log("Changing theme from dark to light")
        document.body.classList.remove("darkmode");
        setTheme("light");
        setThemeValue(false)
        break;
    }
  }
  }} />
        <Route
          exact
          path="/" component={SortingVisualizer}
        />
        <Route
          path="/sv" component={SortingVisualizer}
        />
        <Route path="/ml" component={MachineLearning} />

      </HashRouter>
    </div>
  );
}

export default App;
