import React from "react";
import "./CovidTracker.scss";
import { Line } from "react-chartjs-2";

// Change this value for the speed of the animations.

// Change this value for the number of bars (value) in the array.

// This is the main color of the array bars.

export default class CovidTracker extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      summary: [],
      countries: [],
      country_data: [],
      labels: [],
      datasets: [],
      currentCountry: "IRAN",
    };
  }
  componentDidMount() {
    fetch("https://api.covid19api.com/all")
      .then((response) => {
        return response.json();
      })
      .then((all) => {
        let countries = [];
        const country_data = [];
        all.forEach((element) => {
          if (!countries.includes(element.Country)) {
            countries.push(element.Country);
            country_data.push({
              country: element.Country,
              confirmed: [element.Confirmed],
              deaths: [element.Deaths],
              recovered: [element.Recovered],
              active: [element.Active],
              dates: [element.Date],
            });
          } else {
            country_data[country_data.length - 1].confirmed.push(
              element.Confirmed
            );
            country_data[country_data.length - 1].deaths.push(element.Deaths);
            country_data[country_data.length - 1].recovered.push(
              element.Recovered
            );
            country_data[country_data.length - 1].active.push(element.Active);
            country_data[country_data.length - 1].dates.push(element.Date);
          }
        });
        this.setState({ countries });
        this.setState({ country_data });

        let rand_country = 25; // Math.random();
        const select_ = country_data.filter((element) => {
          return element.country === "United States of America";
        });
        const labels = select_.dates;
        console.log(labels);
        const datasets = [
          {
            label: "#Confirmed",
            fill: false,
            lineTension: 0.5,
            backgroundColor: "rgba(33,47,61,1)",
            borderColor: "rgba(33,47,61,1)",
            borderWidth: 3,

            data: this.state.country_data[rand_country].confirmed,
          },
          {
            label: "#Deaths",
            fill: false,
            lineTension: 0.5,
            backgroundColor: "rgba(205,92,92,1)",
            borderColor: "rgba(205,92,92,1)",
            borderWidth: 3,
            data: this.state.country_data[rand_country].deaths,
          },
          {
            label: "#Recovered",
            fill: false,
            lineTension: 0.5,
            backgroundColor: "rgba(72,201,176,1)",
            borderColor: "rgba(72,201,176,1)",
            borderWidth: 3,

            data: this.state.country_data[rand_country].recovered,
          },
        ];
        this.setState({ labels, datasets });
      });
  }

  render() {
    return (
      <React.Fragment>
        <div className="grid-container">
          <div className="header">
            <h1>Covid19 Tracker App</h1>
          </div>
          <div className="middle">
            <Line
              data={this.state}
              options={{
                title: {
                  display: true,
                  text: "Daily statistics for " + this.state.currentCountry,
                  fontSize: 20,
                },
                legend: {
                  display: true,
                  position: "right",
                },
              }}
            />
          </div>
        </div>
        <div className="footer">
          <p>
            <b>
              Visualizing Covid19 data, Developed with React Framework. Written
              by Ashkan Bashiri.
            </b>
          </p>
        </div>
      </React.Fragment>
    );
  }
}
