import React from "react";
import { MDBDataTable } from "mdbreact";
export default class CovidSummary extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
    };
  }

  componentDidMount() {
    fetch("https://api.covid19api.com/summary")
      .then((response) => {
        return response.json();
      })
      .then((all) => {
        let global = {
          Country: "Global",
          TotalConfirmed: all.Global.TotalConfirmed,
          NewConfirmed: all.Global.NewConfirmed,
          TotalDeaths: all.Global.TotalDeaths,
          NewDeaths: all.Global.NewDeaths,
          TotalRecovered: all.Global.TotalRecovered,
          NewRecovered: all.Global.NewRecovered,
        };
      });

    const data = {
      columns: [
        {
          label: "Name",
          field: "name",
          sort: "asc",
          width: 150,
        },
      ],
      rows: [
        {
          name: "Tiger Nixon",
          position: "System Architect",
          office: "Edinburgh",
          age: "61",
          date: "2011/04/25",
          salary: "$320",
        },
      ],
    };
    this.setState({ data });
  }
  render() {
    return <MDBDataTable striped bordered small data={this.state.data} />;
  }
}
